clearcars = dlmread("cars.csv", ",", 1, 1);matrix = cars(:,1:6);mpg = cars(:,7);m = length(mpg);i = 1;j = 1;denormedRmseArray = []; alpha = [0.01; 0.1; 1.0; 2.0];rand("seed", 0.2013); theta = rand(7, 1);function [normX] =  normalize(x)  normX = (x .- min(x)) ./ (max(x) .- min(x));endfunctiony = normalize(mpg);normMatrix = normalize(matrix);onesColumn = ones(length(normMatrix),1);prepMatrixWithOnes = [onesColumn normMatrix];while (i <= length(alpha))  bestTheta = theta;    while (j <= 100)      h = prepMatrixWithOnes * bestTheta;      diff = h - y;      delta0 = prepMatrixWithOnes' * diff;      minifiedDelta = delta0 * (alpha(i,1) / m);      new = bestTheta - minifiedDelta;       normedRmse = sum(prepMatrixWithOnes .* new', 2);      denormedRmse = normedRmse .* (max(mpg) .- min(mpg)) .+ min(mpg);      rmse = sqrt(sum((denormedRmse .- mpg) .^2) / length(denormedRmse));      denormedRmseArray(j,i) = rmse;      bestTheta = new;      j++;    endwhile  j = 1;  i++;endwhilex = [0:99];plot(x,(denormedRmseArray(:,1)),x,(denormedRmseArray(:,2)),x,(denormedRmseArray(:,3)),x,(denormedRmseArray(:,4)));xlim([0 100]);ylim([0 100]);xlabel('iterations');ylabel('rmse');legend(['alpha ', num2str(alpha(1,1))], ['alpha ', num2str(alpha(2,1))], ['alpha ', num2str(alpha(3,1))], ['alpha ', num2str(alpha(4,1))]);